model.Vendor.entityMethods.createDiscount = function(title, value, description) {	// create discount    try {        discount = require('discount').create(title, discount, text);    } catch (e) {        console.error('[vendor.createDiscount] Failed creating discount:', title, discount, text);        return e;    }	// broadcast discount	require('broadcast').send(this.clients, 'discount', discount);	// mail discount	this.clients.forEach(function (client) {	    client.sendMail(this.email, title, discount);	});};model.Vendor.entityMethods.createDiscount.scope = "public";